import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import styled from "styled-components";

import SideNavBar from "./components/sidenavbar";
import { TestNavBar } from "./components/testNav";

import Discover from "./pages/discover";

import "./css/app.css";
import useMediaQuery from "./utils/useMediaQuery";

export default function App(props: any) {
  const [activeSideBar, setActiveSideBar] = useState<boolean>(true);
  const toggleNavBar = () => setActiveSideBar(!activeSideBar);

  const isMobile = useMediaQuery("(max-width: 480px)");
  console.log("isMobile", isMobile)

  useEffect(() => {
    setActiveSideBar(isMobile);
  },[]);

  return (
    <Router>
      <PageContainer>
        <SideNavBar activeSideBar={activeSideBar} />
        {/* <TestNavBar /> */}
        <ContentWrapper>
          <Switch>
            <Route path="/discover" component={Discover} {...props} />
          </Switch>
        </ContentWrapper>
      </PageContainer>
    </Router>
  );
}

const breakpoints = {
  mobile: "480px",
  desktop: "1024px",
};

const media = {
  mobile: `(max-width: ${breakpoints.mobile})`,
  desktop: `(max-width: ${breakpoints.desktop})`,
};

const ContentWrapper = styled.main`
  padding-left: 280px;

  @media ${media.mobile} {
    padding-left: 10px;
    padding-right: 10px;
  }
`;

const PageContainer = styled.main`
  overflow-x: hidden;
`;
