import React from "react";
import styled, { css } from "styled-components";
import * as colors from "../../colors";
import useMediaQuery from "../../utils/useMediaQuery";

type MovieItemProps = {
  movie: {
    poster_path: string;
    title: string;
    vote_average: number;
    overview: string;
    release_date: string;
    genre_ids: number[];
  };
  genreList: { id: number; name: string }[];
};

export default function MovieItem({ movie, genreList }: MovieItemProps) {
  const isMobile: boolean = useMediaQuery("(max-width: 480px)")
  const getGenreLabels = (
    ids: number[],
    genreList: { id: number; name: string }[]
  ): string => {
    const unformattedLabels = ids.map((id: number) => {
      const genre = genreList.find((genre) => genre.id === id);
      return genre ? genre.name : "Unknown";
    });
    const formattedLabels = unformattedLabels.join(" | ");
    return formattedLabels;
  };

  const applyLastLineFade = (text: string) => {
    const words = text.split(" ");
    let lines = [];
    let currentLine = "";

    // Create a temporary div to measure text
    const tempDiv = document.createElement("div");
    tempDiv.style.position = "absolute";
    tempDiv.style.visibility = "hidden";
    tempDiv.style.width = "300px";
    document.body.appendChild(tempDiv);

    const doesLineFit = (line: any) => {
      tempDiv.innerHTML = line;
      return tempDiv.offsetWidth <= 300;
    };

    words.forEach((word) => {
      const testLine = currentLine ? `${currentLine} ${word}` : word;
      if (doesLineFit(testLine)) {
        currentLine = testLine;
      } else {
        lines.push(currentLine);
        currentLine = word;
      }
    });

    if (currentLine) lines.push(currentLine);
    document.body.removeChild(tempDiv);

    if (lines.length > 1) {
      const lastLine = lines.pop();
      return (
        <>
          {lines.join(" ")} <LastLineFade>{lastLine}</LastLineFade>
        </>
      );
    }
    return <LastLineFade>{text}</LastLineFade>;
  };

  return (
    // The MovieItemWrapper must be linked to the movie details popup
    <MovieItemWrapper>
      <LeftCont>
        <MoviePoster
          alt="Movie poster image"
          src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`}
        />
      </LeftCont>
      <RightCont>
        <MovieHeader>
          <MovieTitle>{movie.title}</MovieTitle>
          <RatingBox>{movie.vote_average.toFixed(1)}</RatingBox>
        </MovieHeader>
        <GenreRow>
          <Genre>{getGenreLabels(movie.genre_ids, genreList)}</Genre>
        </GenreRow>
        <DescriptionContainer>
          <MovieDescription>{isMobile ? applyLastLineFade(movie.overview) : movie.overview}</MovieDescription>
          <MovieRelease>{movie.release_date}</MovieRelease>
        </DescriptionContainer>
      </RightCont>
    </MovieItemWrapper>
  );
}

const breakpoints = {
  mobile: "480px",
  desktop: "1024px",
};

const media = {
  mobile: `(max-width: ${breakpoints.mobile})`,
  desktop: `(max-width: ${breakpoints.desktop})`,
};

const MovieItemWrapper = styled.div`
  position: relative;
  background-color: white;
  border-radius: 3px;
  display: grid;
  grid-template-columns: 0.5fr 2fr;
  grid-gap: 2px;
  padding: 20px;
`;

const LeftCont = styled.div`
  display: inline-block;
`;

const MoviePoster = styled.img`
  width: 100%;
  height: auto;
`;

const RightCont = styled.div`
  display: inline-block;
`;

const MovieHeader = styled.div`
  display: flex;
  justify-content: space-between;
  padding-left: 15px;
  padding-right: 15px;
  align-items: center;
`;

const MovieTitle = styled.div`
  font-size: 1.3em;
  font-weight: 900;
  color: ${colors.fontColor};
`;

const RatingBox = styled.div`
  font-weight: 700;
  color: white;
  background-color: ${colors.primaryColor};
  padding: 3px;
  border-radius: 5px;
`;

const GenreRow = styled.div`
  display: flex;
  align-items: center;
  padding-left: 15px;

  @media ${media.mobile} {
    padding-top: 5px;
  }
`;

const Genre = styled.div`
  font-size: 1em;
  font-weight: 700;
  color: ${colors.primaryColor};

  @media ${media.mobile} {
    font-size: 0.7em;
    font-weight: 700;
  }
`;

const DescriptionContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const MovieDescription = styled.div`
  display: inline-block;
  padding-top: 10px;
  padding-left: 15px;
  padding-right: 15px;
  color: ${colors.fontColor};

  /* @media ${media.mobile} {
    background: linear-gradient(to right, #000 50%, rgba(0, 0, 0, 0) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    height: 100px;
    overflow: hidden;
  } */
`;

const LastLineFade = styled.span`
  background: linear-gradient(
    to right,
    rgba(0, 0, 0, 1) 0%,
    rgba(0, 0, 0, 0) 100%
  );
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
`;

const MovieRelease = styled.div`
  padding-top: 40px;
  padding-left: 15px;
  font-size: 0.8em;
  font-weight: 500;
  color: ${colors.primaryColor};
`;
