import React from "react";
import styled from "styled-components";
import * as colors from "../../colors";

interface MovieTitleProps {
  title: string;
}

type MovieItemProps = {
  movie: {
    title: string;
    rating: number;
    overview: string;
    releaseDate: string;
    genres: number[];
  };
  genres?: [];
};

export default function MovieItem({ movie, genres }: MovieItemProps) {
  console.log("movie in MovieItem", movie);
  return (
    // The MovieItemWrapper must be linked to the movie details popup
    <MovieItemWrapper>
      <LeftCont>Holding image</LeftCont>
      <RightCont>
        <MovieHeader>
          <div>Hi</div>
          {/* <MovieTitle title={movie.title} /> */}
          <div>test</div>
        </MovieHeader>
      </RightCont>
    </MovieItemWrapper>
  );
}

const MovieItemWrapper = styled.div`
  position: relative;
  background-color: white;
  border-radius: 3px;
  display: grid;
  grid-template-columns: 0.5fr 2fr;
  grid-gap: 1px;
`;

const LeftCont = styled.div`
  display: inline-block;
`;

const RightCont = styled.div`
  display: inline-block;
`;

const MovieHeader = styled.div`
  display: flex;
  justify-content: space-between;
  padding-left: 15px;
  padding-right: 15px;
`;

const MovieTitle = styled.div<MovieTitleProps>`
  font-size: 1.6em;
  font-weight: 700;
  color: ${colors.fontColor};
`;
