import React from "react";
import styled, { css } from "styled-components";
import * as colors from "../../colors";
import ExpandableFilters from "../expandablefilters";
import SearchBar from "../searchbar";

interface SearchFiltersContProps {
  marginBottom?: boolean;
}

type SearchFiltersProps = {
  genres: {
    id: number;
    name: string;
  }[];
  ratings: {
    id: number;
    name: number;
  }[];
  languages: {
    id: string;
    name: string;
  }[];
  searchMovies: (keyword: string | undefined, year: number | undefined) => void;
};

export default function SearchFilters({
  genres,
  ratings,
  languages,
  searchMovies,
}: SearchFiltersProps) {
  return (
    <FiltersWrapper>
      <SearchFiltersCont marginBottom className="search_inputs_cont">
        <SearchBar searchMovies={searchMovies} />
      </SearchFiltersCont>
      <ExpandableFiltersCont className="expandable_filters_cont">
        <CategoryTitle>Movies</CategoryTitle>
        <ExpandableFilters
          genres={genres}
          ratings={ratings}
          languages={languages}
          searchMovies={searchMovies}
        />
      </ExpandableFiltersCont>
    </FiltersWrapper>
  );
}

const breakpoints = {
  mobile: "480px",
  desktop: "1024px",
};

const media = {
  mobile: `(max-width: ${breakpoints.mobile})`,
  desktop: `(max-width: ${breakpoints.desktop})`,
};

const FiltersWrapper = styled.div`
  position: relative;
`;

const SearchFiltersCont = styled.div<SearchFiltersContProps>`
  background-color: white;
  padding: 20px;
  border-radius: 3px;
  transition: all 0.3s ease-in-out;

  ${(props) =>
    props.marginBottom &&
    css`
      margin-bottom: 15px;
    `}

  @media ${media.mobile} {
    background-color: ${colors.lightBackground};
  }
`;

const ExpandableFiltersCont = styled.div`
  background-color: white;
  padding: 20px;
  border-radius: 3px;
  transition: all 0.3s ease-in-out;

  @media ${media.mobile} {
    display: none;
  }
`;

const CategoryTitle = styled.div`
  margin-bottom: 10px;
`;
