import React from "react";
import styled, { css } from "styled-components";
import * as colors from "../../colors";

interface MoviePosterProps {
  imageUrl: string;
}

type MovieItemProps = {
  movie: {
    poster_path: string;
    title: string;
    vote_average: number;
    overview: string;
    release_date: string;
    genre_ids: number[];
  };
  genreList: {id: number, name:string}[];
};

export default function MovieItem({ movie, genreList }: MovieItemProps) {
  console.log("movie in MovieItem", movie);

  const getGenreLabels = (ids: number[], genreList: {id: number, name:string}[]): string => {
    const unformattedLabels =  ids.map((id: number) => {
      const genre = genreList.find((genre) => genre.id === id);
      return genre ? genre.name : "Unknown";
    });
    const formattedLabels = unformattedLabels.join (" | ");
    return formattedLabels;
  };

  const genreLabels = getGenreLabels(movie.genre_ids, genreList)
  console.log("genreLabels", genreLabels)

  return (
    // The MovieItemWrapper must be linked to the movie details popup
    <MovieItemWrapper>
      <LeftCont>
        <MoviePoster src={`https://api.themoviedb.org/${movie.poster_path}`} />
      </LeftCont>
      <RightCont>
        <MovieHeader>
          <MovieTitle>{movie.title}</MovieTitle>
          <RatingBox>{movie.vote_average.toFixed(1)}</RatingBox>
        </MovieHeader>
        <GenreRow>
          <Genre>{genreLabels}</Genre>
        </GenreRow>
        <MovieDescription>{movie.overview}</MovieDescription>
        <MovieRelease>{movie.release_date}</MovieRelease>
      </RightCont>
    </MovieItemWrapper>
  );
}

const MovieItemWrapper = styled.div`
  position: relative;
  background-color: white;
  border-radius: 3px;
  display: grid;
  grid-template-columns: 0.5fr 2fr;
  grid-gap: 1px;
  padding: 10px;
`;

const LeftCont = styled.div`
  display: inline-block;
`;

const MoviePoster = styled.img`
  width: 100%;
  height: auto;
`;

