import React, { useState } from "react";
import styled, { css } from "styled-components";
import * as colors from "../../colors";

import Checkbox from "../checkbox";

interface ExpandableIconProps {
  minus?: boolean;
  plus?: boolean;
}

type ExpandableFiltersProps = {
  // genres, ratings, languages, searchMovies
  genres: {
    id: number;
    name: string;
  }[];
  ratings: {
    id: number;
    name: number;
  }[];
  languages: {
    id: string;
    name: string;
  }[];
};

export default function ExpandableFilters({
  genres,
  ratings,
  languages,
}: ExpandableFiltersProps) {
  const [filtersShown, setFiltersShown] = useState<boolean>(false);
  const [showGenres, setShowGenres] = useState<boolean>(false);
  const [showMinVote, setShowMinVote] = useState<boolean>(false);
  const [showLanguage, setShowLanguage] = useState<boolean>(false);

  // You need to create your own checkbox component with a custom checkmark

  return (
    <>
      <SectionHeader>
        <ExpandableIcon minus />
        <SectionLabel>Select genre(s)</SectionLabel>
      </SectionHeader>
      <ExpandableSection>
        {genres.map((genre) => {
          return <Checkbox genreTitle={genre.name} genreId={genre.id} />;
        })}
      </ExpandableSection>
    </>
  );
}

const SectionHeader = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
  padding-left: 3px
`;

const SectionLabel = styled.div`
  font-weight: 200;
`;

const ExpandableSection = styled.div``;

const ExpandableIcon = styled.button<ExpandableIconProps>`
  position: relative;
  width: 15px;
  height: 15px;
  background: none;
  border: none;
  cursor: pointer;
  padding: 0;

  ${(props) =>
    props.plus &&
    css`
      &::before,
      &::after {
        content: "";
        position: absolute;
        background-color: ${colors.fontColor};
      }

      &::before {
        top: 50%;
        left: 0;
        width: 100%;
        height: 2px;
        transform: translateY(-50%);
      }

      &::after {
        top: 0;
        left: 50%;
        width: 2px;
        height: 100%;
        transform: translateX(-50%);
      }
    `}

  ${(props) =>
    props.minus &&
    css`
      &::before,
      &::after {
        content: "";
        position: absolute;
        background-color: ${colors.fontColor};
      }

      &::before {
        top: 50%;
        left: 0;
        width: 100%;
        height: 2px;
        transform: translateY(-50%);
      }
    `}
`;
