import React from "react";
import styled, { css } from "styled-components";
import ExpandableFilters from "../expandablefilters";
import SearchBar from "../searchbar";
import useMediaQuery from "../../utils/useMediaQuery";

interface SearchFiltersContProps {
  marginBottom?: boolean;
  isMobile?: boolean
}

type SearchFiltersProps = {
  genres: {
    id: number;
    name: string;
  }[];
  ratings: {
    id: number;
    name: number;
  }[];
  languages: {
    id: string;
    name: string;
  }[];
  searchMovies: (keyword: string | undefined, year: number | undefined) => void;
};

export default function SearchFilters({
  genres,
  ratings,
  languages,
  searchMovies,
}: SearchFiltersProps) {
  const isMobile = useMediaQuery("(max-width: 480px)");
  return (
    <FiltersWrapper>
      <SearchFiltersCont marginBottom isMobile className="search_inputs_cont">
        <SearchBar searchMovies={searchMovies} />
      </SearchFiltersCont>
      <SearchFiltersCont>
        <CategoryTitle>Movies</CategoryTitle>
        <ExpandableFilters
          genres={genres}
          ratings={ratings}
          languages={languages}
        />
      </SearchFiltersCont>
    </FiltersWrapper>
  );
}

const FiltersWrapper = styled.div`
  position: relative;
`;

const SearchFiltersCont = styled.div<SearchFiltersContProps>`
  background-color: white;
  padding: 20px;
  border-radius: 3px;
  transition: all 0.3s ease-in-out;

  ${(props) =>
    props.marginBottom &&
    css`
      margin-bottom: 15px;
    `}

  isMobile {
    display: none;
  }
`;

const CategoryTitle = styled.div`
  margin-bottom: 10px;
`;
