import React from "react";
import styled from "styled-components";
import * as colors from "../../colors";

type MovieItemProps = {
  movie: {
    title: string;
    vote_average: number;
    overview: string;
    releaseDate: string;
    genres: number[];
  };
  genres?: [];
};

export default function MovieItem({ movie, genres }: MovieItemProps) {
  console.log("movie in MovieItem", movie);
  return (
    // The MovieItemWrapper must be linked to the movie details popup
    <MovieItemWrapper>
      <LeftCont>Holding image</LeftCont>
      <RightCont>
        <MovieHeader>
          <MovieTitle>{movie.title}</MovieTitle>
          <RatingBox>{movie.vote_average.toFixed(1)}</RatingBox>
        </MovieHeader>
        <GenreRow>
          <Genre>Comedy</Genre>
        </GenreRow>
      </RightCont>
    </MovieItemWrapper>
  );
}

const MovieItemWrapper = styled.div`
  position: relative;
  background-color: white;
  border-radius: 3px;
  display: grid;
  grid-template-columns: 0.5fr 2fr;
  grid-gap: 1px;
`;

const LeftCont = styled.div`
  display: inline-block;
`;

const RightCont = styled.div`
  display: inline-block;
`;

const MovieHeader = styled.div`
  display: flex;
  justify-content: space-between;
  padding-left: 15px;
  padding-right: 15px;
  align-items: center;
`;

const MovieTitle = styled.div`
  font-size: 1.3em;
  font-weight: 900;
  color: ${colors.fontColor};
`;

const RatingBox = styled.div`
  font-weight: 700;
  color: white;
  background-color: ${colors.primaryColor};
  padding: 3px;
  border-radius: 5px;
`;

const GenreRow = styled.div`
  display: flex;
  align-items: center;
  padding-left: 15px;
`;

const Genre = styled.div`
  font-size: 1em;
  font-weight: 700;
  color: ${colors.primaryColor};
`;
